mapper.select:
select
num from user where
userid=#{userId}

mapper.update:
UPDATE user
SET email =
#{email}, phone = #{phone}
WHERE num = #{num}

mapper.select:
SELECT * FROM rentcar

	total_qty > 0

		AND category = #{category}


		AND company = #{company}

mapper.update:
UPDATE rentcar
SET total_qty
=
#{qty}
WHERE num = #{num}

mapper.select:
SELECT reservation.reserve_seq AS regNo, user.name AS name, rentcar.price AS price, rentcar.img AS img, reservation.qty AS qty, reservation.dday AS dday, reservation.rday AS rday, reservation.usein AS usein, reservation.usewifi AS usewifi, reservation.useseat AS useseat, reservation.usenavi AS usenavi, (reservation.qty * reservation.dday * (rentcar.price + (reservation.usein + reservation.usewifi + reservation.useseat)*10000)) AS totalPrice, DATE_ADD(STR_TO_DATE(reservation.rday, '%Y-%m-%d'), INTERVAL (reservation.dday - 1) DAY) AS endDate FROM reservation JOIN rentcar ON reservation.num = rentcar.num JOIN user ON reservation.userid = user.userid
user.userid = #{userId}
;

mapper.update:
UPDATE reservation
SET num = #{num}, userid = #{userid}, qty = #{qty}, dday = #{dday},
rday = #{rday}, usein = #{usein}, usewifi = #{usewifi}, usenavi=
#{usenavi},useseat = #{useseat}
WHERE reserve_seq=#{reserveSeq};
